// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title RedPacketDemo
 * @notice 演示模式红包合约 - 不限制领取次数，方便演示
 * @dev 用于 AI 春晚等活动演示，用户可无限次领取小额红包
 */
contract RedPacketDemo {
    // ============ 状态变量 ============

    address public owner;
    uint256 public fixedAmount;      // 固定领取金额
    uint256 public maxPerClaim;      // 单次最大金额（防误操作）
    uint256 public totalClaimed;     // 总领取次数
    uint256 public totalDistributed; // 总发放金额

    // 记录每个地址领取次数（仅用于统计，不限制）
    mapping(address => uint256) public claimCount;

    // ============ 事件 ============

    event Deposit(address indexed sender, uint256 amount);
    event Claim(address indexed user, uint256 amount, uint256 claimIndex);
    event ConfigUpdated(uint256 fixedAmount, uint256 maxPerClaim);
    event EmergencyWithdraw(uint256 amount);

    // ============ 修饰符 ============

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    // ============ 构造函数 ============

    constructor(uint256 _fixedAmount, uint256 _maxPerClaim) {
        owner = msg.sender;
        fixedAmount = _fixedAmount;
        maxPerClaim = _maxPerClaim;
        totalClaimed = 0;
        totalDistributed = 0;
    }

    // ============ 核心函数 ============

    /**
     * @notice 充值 CFX 到红包合约
     */
    function deposit() external payable onlyOwner {
        require(msg.value > 0, "Must send CFX");
        emit Deposit(msg.sender, msg.value);
    }

    /**
     * @notice 领取红包 - 演示模式，无限次领取
     * @dev 每个用户可无限次领取固定金额，直到合约余额不足
     */
    function claim() external {
        uint256 contractBalance = address(this).balance;
        require(contractBalance >= fixedAmount, "Insufficient balance");

        uint256 amount = fixedAmount;

        // 如果合约余额不足固定金额，发放剩余全部
        if (contractBalance < fixedAmount) {
            amount = contractBalance;
        }

        // 更新统计
        claimCount[msg.sender]++;
        totalClaimed++;
        totalDistributed += amount;

        // 转账给用户
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");

        emit Claim(msg.sender, amount, claimCount[msg.sender]);
    }

    /**
     * @notice 批量领取红包 - 一次性领取多次（方便演示）
     * @param times 领取次数
     */
    function claimBatch(uint256 times) external {
        require(times > 0 && times <= 100, "Times must be 1-100");

        uint256 totalAmount = fixedAmount * times;
        uint256 contractBalance = address(this).balance;

        require(contractBalance >= totalAmount, "Insufficient balance for batch");

        // 更新统计
        claimCount[msg.sender] += times;
        totalClaimed += times;
        totalDistributed += totalAmount;

        // 转账给用户
        (bool success, ) = msg.sender.call{value: totalAmount}("");
        require(success, "Transfer failed");

        emit Claim(msg.sender, totalAmount, claimCount[msg.sender]);
    }

    /**
     * @notice 设置红包金额参数
     */
    function setAmount(uint256 _fixedAmount, uint256 _maxPerClaim) external onlyOwner {
        require(_fixedAmount > 0 && _maxPerClaim >= _fixedAmount, "Invalid amount");
        fixedAmount = _fixedAmount;
        maxPerClaim = _maxPerClaim;
        emit ConfigUpdated(_fixedAmount, _maxPerClaim);
    }

    /**
     * @notice 紧急提取所有余额
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance");

        (bool success, ) = owner.call{value: balance}("");
        require(success, "Transfer failed");

        emit EmergencyWithdraw(balance);
    }

    /**
     * @notice 转移所有权
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        owner = newOwner;
    }

    // ============ 视图函数 ============

    /**
     * @notice 获取合约余额
     */
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @notice 获取用户领取次数
     */
    function getClaimCount(address user) external view returns (uint256) {
        return claimCount[user];
    }

    /**
     * @notice 获取合约统计信息
     */
    function getStats() external view returns (
        uint256 balance,
        uint256 fixedAmount_,
        uint256 totalClaimed_,
        uint256 totalDistributed_,
        uint256 remainingClaims
    ) {
        balance = address(this).balance;
        fixedAmount_ = fixedAmount;
        totalClaimed_ = totalClaimed;
        totalDistributed_ = totalDistributed;
        if (fixedAmount > 0) {
            remainingClaims = balance / fixedAmount;
        } else {
            remainingClaims = 0;
        }
    }

    /**
     * @notice 估算还能领取多少次
     */
    function getRemainingClaims() external view returns (uint256) {
        if (fixedAmount == 0) return 0;
        return address(this).balance / fixedAmount;
    }

    // 接收 CFX
    receive() external payable {
        emit Deposit(msg.sender, msg.value);
    }
}

